select.where:
is_del = 0

    AND id = #{id}


    AND accounting_period = #{accountingPeriod}


    AND actuarial_code like concat('%', #{actuarialCode}, '%')


    AND business_code like concat('%', #{businessCode}, '%')


    AND product_name like concat('%', #{productName}, '%')


    AND term_type = #{termType}


    AND design_type = #{designType}


    AND short_term_flag = #{shortTermFlag}

mapper.update:
update t_base_accounting_reserve_detail
accounting_period = #{accountingPeriod}, actuarial_code = #{actuarialCode}, business_code = #{businessCode}, product_name = #{productName}, term_type = #{termType}, design_type = #{designType}, short_term_flag = #{shortTermFlag}, valid_policy_count = #{validPolicyCount}, accumulated_premium = #{accumulatedPremium}, account_value = #{accountValue}, dividend_provision = #{dividendProvision}, best_estimate = #{bestEstimate}, risk_margin = #{riskMargin}, residual_margin = #{residualMargin}, unmodeled_reserve = #{unmodeledReserve}, waiver_reserve = #{waiverReserve}, persistence_bonus_reserve = #{persistenceBonusReserve}, long_term_unearned = #{longTermUnearned}, short_term_unearned = #{shortTermUnearned}, unearned_premium_reserve = #{unearnedPremiumReserve}, outstanding_claim_reserve = #{outstandingClaimReserve}, total_accounting_reserve = #{totalAccountingReserve}, remark = #{remark}, update_by = #{updateBy}, update_time = #{updateTime},
where id = #{id}
