select.where:
is_del = 0

    AND id = #{id}


    AND accounting_period = #{accountingPeriod}


    AND actuarial_code like concat('%', #{actuarialCode}, '%')


    AND business_code like concat('%', #{businessCode}, '%')


    AND product_name like concat('%', #{productName}, '%')


    AND short_term_flag = #{shortTermFlag}

mapper.update:
update t_base_dividend_fund_cost_rate
accounting_period = #{accountingPeriod}, actuarial_code = #{actuarialCode}, business_code = #{businessCode}, product_name = #{productName}, short_term_flag = #{shortTermFlag}, guaranteed_cost_rate = #{guaranteedCostRate}, investment_return_rate = #{investmentReturnRate}, dividend_ratio = #{dividendRatio}, fund_cost_rate_t0 = #{fundCostRateT0}, fund_cost_rate_t1 = #{fundCostRateT1}, fund_cost_rate_t2 = #{fundCostRateT2}, fund_cost_rate_t3 = #{fundCostRateT3}, remark = #{remark}, update_by = #{updateBy}, update_time = #{updateTime},
where id = #{id}
