mapper.select:


    and id = #{id}
    and account_period = #{accountPeriod}
    and cash_flow_type = #{cashFlowType}
    and bp_type = #{bpType}
    and duration_type = #{durationType}
    and design_type = #{designType}
    and is_short_term = #{isShortTerm}
    and is_del = 0

order by create_time desc



    and account_period = #{accountPeriod}
    and cash_flow_type = #{cashFlowType}
    and duration_type = #{durationType}
    and design_type = #{designType}
    and is_short_term = #{isShortTerm}
    and is_del = 0

limit 1

mapper.insert:
insert into t_dur_liability_cash_flow_summary (
    account_period, cash_flow_type, bp_type, duration_type, design_type, is_short_term, 
    cash_val_set, present_cash_val_set, create_by, update_by
) values (
    #{accountPeriod}, #{cashFlowType}, #{bpType}, #{durationType}, #{designType}, #{isShortTerm}, 
    #{cashValSet}, #{presentCashValSet}, #{createBy}, #{updateBy}
)

mapper.update:
update t_dur_liability_cash_flow_summary
account_period = #{accountPeriod}, cash_flow_type = #{cashFlowType}, bp_type = #{bpType}, duration_type = #{durationType}, design_type = #{designType}, is_short_term = #{isShortTerm}, cash_val_set = #{cashValSet}, present_cash_val_set = #{presentCashValSet}, update_by = #{updateBy}, update_time = CURRENT_TIMESTAMP
where id = #{id}
