mapper.select:


    is_del = 0

        AND account_period = #{accountPeriod}


        AND cash_flow_type = #{cashFlowType}


        AND duration_type = #{durationType}


        AND design_type = #{designType}


        AND is_short_term = #{isShortTerm}


order by account_period desc, cash_flow_type, duration_type, design_type, is_short_term



    is_del = 0

        AND account_period = #{accountPeriod}


        AND cash_flow_type = #{cashFlowType}


        AND duration_type = #{durationType}


        AND design_type = #{designType}


        AND is_short_term = #{isShortTerm}


limit 1

mapper.insert:
insert into t_dur_liability_duration_summary (
    account_period, cash_flow_type, duration_type, design_type, is_short_term,
    duration_val_set, create_by, update_by
) values (
    #{accountPeriod}, #{cashFlowType}, #{durationType}, #{designType}, #{isShortTerm},
    #{durationValSet}, #{createBy}, #{updateBy}
)

insert into t_dur_liability_duration_summary (
    account_period, cash_flow_type, duration_type, design_type, is_short_term,
    duration_val_set, create_by, update_by
) values

    (
    #{item.accountPeriod}, #{item.cashFlowType}#{item.durationType},
    #{item.designType}, #{item.isShortTerm}, #{item.durationValSet}, #{item.createBy}, #{item.updateBy}
    )

mapper.update:
update t_dur_liability_duration_summary set is_del = 1, update_time = now() where id in
#{id}
and is_del = 0
