select.where:
is_del = 0

    AND accounting_period = #{accountingPeriod}


    AND actuarial_code like concat('%', #{actuarialCode}, '%')


    AND business_code like concat('%', #{businessCode}, '%')


    AND product_name like concat('%', #{productName}, '%')


    AND term_type = #{termType}


    AND insurance_main_type = #{insuranceMainType}


    AND insurance_sub_type = #{insuranceSubType}


    AND design_type = #{designType}


    AND short_term_flag = #{shortTermFlag}


    AND reg_mid_id = #{regMidId}


    AND new_business_flag = #{newBusinessFlag}

mapper.update:
update t_base_product_attribute
accounting_period = #{accountingPeriod}, actuarial_code = #{actuarialCode}, business_code = #{businessCode}, product_name = #{productName}, term_type = #{termType}, insurance_main_type = #{insuranceMainType}, insurance_sub_type = #{insuranceSubType}, design_type = #{designType}, short_term_flag = #{shortTermFlag}, reg_mid_id = #{regMidId}, guaranteed_cost_rate = #{guaranteedCostRate}, sub_account = #{subAccount}, new_business_flag = #{newBusinessFlag}, remark = #{remark}, update_by = #{updateBy}, update_time = #{updateTime},
where id = #{id}
