mapper.select:
SELECT correlation_value
FROM t_minc_correlation_coef
WHERE item_code_x = #{itemCodeX}
  AND item_code_y = #{itemCodeY}
  AND accounting_period = #{accountingPeriod}

mapper.update:
UPDATE t_minc_marginal_capital
SET sub_risk_marginal_factor = #{subRiskMarginalFactor},
    update_by = 'system',
    update_time = NOW()
WHERE item_code = #{itemCode}
  AND accounting_period = #{accountingPeriod}

mapper.select:
SELECT sub_risk_marginal_factor
FROM t_minc_marginal_capital
WHERE item_code = #{itemCode}
  AND accounting_period = #{accountingPeriod}
  AND sub_risk_marginal_factor IS NOT NULL

mapper.update:
UPDATE t_minc_marginal_capital
SET company_marginal_factor = #{companyMarginalFactor},
    update_by = 'system',
    update_time = NOW()
WHERE item_code = #{itemCode}
  AND accounting_period = #{accountingPeriod}
